import React, { useState, useEffect } from 'react';
import { InputGroup, FormControl, Button, Card } from 'react-bootstrap';
import axios from 'axios';

const API_URL = 'https://realty-mole-property-api.p.rapidapi.com/properties';

const MovieSearch = () => {
  const [query, setQuery] = useState('');
  const [movies, setMovies] = useState([]);

  const fetchMovies = async (searchQuery) => {
    try {
      const response = await axios.get(API_URL, {
        headers: {
          'X-RapidAPI-Key': 'e8e6d1cb20msh3c038ddf785e626p1a61ccjsn3f1d9d72ccb6',
          'X-RapidAPI-Host': 'realty-mole-property-api.p.rapidapi.com',
        },
        params: {
          q: searchQuery,
        },
      });

      setMovies(response.data.d);
    } catch (error) {
      console.error(error);
    }
  };

  useEffect(() => {
    fetchMovies(''); 
  }, []);

  const handleSearch = () => {
    fetchMovies(query); 
  };

  return (
    <div>
      <InputGroup className="mb-4 w-25 d-flex flex-row justify-content-end">
        <FormControl
          placeholder="Search for a movie"
          aria-label="Search for a movie"
          value={query}
          onChange={(e) => setQuery(e.target.value)}
        />
        <Button variant="danger" onClick={handleSearch}>
          Search
        </Button>
      </InputGroup>
      <div className="d-flex flex-row justify-content-start flex-wrap">
        {movies.map((movie) => (
          <Card key={movie.id} style={{ width: '18rem' }} className="m-5">
            <Card.Img variant="top" src={movie.i.imageUrl} />
            <Card.Body>
              <Card.Title>{movie.l}</Card.Title>
              <Card.Text>{movie.s}</Card.Text>
            </Card.Body>
          </Card>
        ))}
      </div>
    </div>
  );
};

