import React, { useState, useEffect } from 'react';
import axios from 'axios';

const About = () => {
  const [items, setItems] = useState([]);

  useEffect(() => {
    const fetchItems = async () => {
      const options = {
        method: 'GET',
        url: 'https://menu7.p.rapidapi.com/menu-api/api/manage-locks',
        params: {
          filter: '{\n  "_sort": [],\n  "_order": [\n    "ASC"\n  ],\n  "_start": 0,\n  "_end": 0,\n  "instance": "",\n  "product": ""\n}'
        },
        headers: {
          Authorization: 'undefined',
          'X-RapidAPI-Key': 'e8e6d1cb20msh3c038ddf785e626p1a61ccjsn3f1d9d72ccb6',
          'X-RapidAPI-Host': 'menu7.p.rapidapi.com'
        }
      };
      
      try {
        const response = await axios.request(options);
        setItems(response.data);
      } catch (error) {
        console.error(error);
      }
    };

    fetchItems();
  }, []);

  const cards = items.map((item) => (
    <div className="card" key={item.id}>
      <div className="card-body">
        <h5 className="card-title">{item.name}</h5>
        <p className="card-text">{item.description}</p>
      </div>
    </div>
  ));

  return (
    <div className="container mt-4">
      <div className="row">
        {cards}
      </div>
    </div>
  );
};

export default About;
