import React, { useState, useEffect } from 'react';
import axios from 'axios';

const API_URL = 'https://realty-mole-property-api.p.rapidapi.com/properties';

const PropertyCards = () => {
  const [properties, setProperties] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    axios.get(API_URL, {
      headers: {
        'x-rapidapi-host': 'realty-mole-property-api.p.rapidapi.com',
        'x-rapidapi-key': 'YOUR_API_KEY_HERE', // Replace with your own API key
      },
    })
      .then((response) => {
        setProperties(response.data);
        setLoading(false);
      })
      .catch((error) => {
        console.log(error);
        setLoading(false);
      });
  }, []);

  if (loading) {
    return <p>Loading properties...</p>;
  }

  return (
    <div className="property-cards-container">
      <div className="row row-cols-1 row-cols-md-3 g-4">
        {properties.map((property) => (
          <div key={property.id} className="col">
            <div className="card h-100">
              <img src={property.images[0].url} className="card-img-top" alt={property.address.fullAddress} />
              <div className="card-body">
                <h5 className="card-title">{property.address.fullAddress}</h5>
                <p className="card-text">{property.description}</p>
                <ul>
                  <li>Price: {property.price}</li>
                  <li>Beds: {property.beds}</li>
                  <li>Baths: {property.baths}</li>
                  <li>Square footage: {property.sqft}</li>
                </ul>
              </div>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default PropertyCards;
