import React, { useState } from 'react';
import axios from 'axios';

const API_KEY = 'e8e6d1cb20msh3c038ddf785e626p1a61ccjsn3f1d9d72ccb6';

function App1() {
  const [searchTerm, setSearchTerm] = useState('');
  const [properties, setProperties] = useState([]);

  const handleSearchInputChange = async (event) => {
    const searchTerm = event.target.value.trim();

    if (searchTerm.length > 0) {
      try {
        const response = await axios.get(`https://realty-mole-property-api.p.rapidapi.com/properties?address=${searchTerm}`, {
          headers: {
            'X-RapidAPI-Key': API_KEY,
            'X-RapidAPI-Host': 'realty-mole-property-api.p.rapidapi.com'
          }
        });

        const properties = response.data.data;
        setProperties(properties);
      } catch (error) {
        console.error(error);
      }
    } else {
      setProperties([]);
    }

    setSearchTerm(searchTerm);
  };

  return (
    <div>
      <input type="text" value={searchTerm} onChange={handleSearchInputChange} placeholder="Search..."/>
      <ul>
        {properties.map((property) => (
          <li key={property.property_id}>{property.address}</li>
        ))}
      </ul>
    </div>
  );
}

export default App1;
