import React,{useState} from 'react'
import Button from 'react-bootstrap/Button';
import Card from 'react-bootstrap/Card';
import {} from '../img/Carouselstyle.css';
import { Col, Container } from 'react-bootstrap';



function ExampleComponent({ items }) {
  const [displayCount, setDisplayCount] = useState(3);

  const handleShowMore = () => {
    setDisplayCount(displayCount + 3);
  };

  return (
    <div>
      {items.slice(0, displayCount).map((item, index) => (
        <div key={index}>
          {/* Render your item here */}
        </div>
      ))}
      {displayCount < items.length && (
        <button onClick={handleShowMore}>Show More</button>
      )}
    </div>
  );
}

const Cards = () => {

const realestate = [
  {
     id: 1,
     name: "Shtepia ne Prishtin",
     description: "Three-storey villa for sale, coht simple f surrounded by a green belt. The condition is very well maintained invested wisely. You are welcome for a visit. ",
     img: "https://media.istockphoto.com/id/1026205392/photo/beautiful-luxury-home-exterior-at-twilight.jpg?s=612x612&w=0&k=20&c=HOCqYY0noIVxnp5uQf1MJJEVpsH_d4WtVQ6-OwVoeDo="
  },
  {
    id: 2,
    name: "Shtepia ne Prizren",
    img: "https://image.shutterstock.com/image-photo/beautiful-exterior-newly-built-luxury-260nw-529108441.jpg"
  },
  {
    id: 3,
    name: "Shtepia ne Ferizaj",
    img: "https://media.istockphoto.com/id/175416231/photo/cute-simple-home.jpg?b=1&s=170667a&w=0&k=20&c=beTWtD7CHAws6SwjxAMFOW7DB5WkgecBygki38giODQ="
  },
];

  return (
    <>
    <Container className='ContainerRow'>
        <Col>
        {realestate.map((real) =>(

      
      <Card style={{ width: '18rem' }}>
      <Card.Img variant="top" src={real.img} />
      <Card.Body>
        <Card.Title>{realestate.id}</Card.Title>
        <Card.Text>
         {real.description}
        </Card.Text>
        <Button variant="primary">Go somewhere</Button>
      </Card.Body>
    </Card>
      ))}

    
    </Col>

    
    </Container>

    </>
  )
}

export default Cards
