import React, { useState, useEffect } from 'react';

const API_URL = 'https://realty-mole-property-api.p.rapidapi.com/properties';

const PropertyList = () => {
  const [properties, setProperties] = useState([]);
  const [loading, setLoading] = useState(true);
  const [searchTerm, setSearchTerm] = useState('');

  useEffect(() => {
    setLoading(true);
    fetch(`${API_URL}?${searchTerm}`, {
      headers: {
        'X-RapidAPI-Key': 'e8e6d1cb20msh3c038ddf785e626p1a61ccjsn3f1d9d72ccb6',
        'X-RapidAPI-Host': 'realty-mole-property-api.p.rapidapi.com' // Replace with your own API key
      },
    })
      .then((response) => response.json())
      .then((data) => {
        setProperties(data);
        setLoading(false);
      });
  }, [searchTerm]);

  const handleSearch = (e) => {
    e.preventDefault();
    const form = e.target;
    const formData = new FormData(form);
    const queryString = new URLSearchParams(formData).toString();
    setSearchTerm(queryString);
    form.reset();
  };

  if (loading) {
    return <p>Loading properties...</p>;
  }

  return (
    <div>
      <form onSubmit={handleSearch}>
        <label htmlFor="city_name">City Name:</label>
        <input type="text" id="city_name" name="city_name" />
        <button type="submit">Search</button>
      </form>
      {properties.map((property) => (
        <div key={property.id}>
          <h2>{property.address.fullAddress}</h2>
          <p>{property.description}</p>
          <ul>
            <li>Price: {property.price}</li>
            <li>Beds: {property.beds}</li>
            <li>Baths: {property.baths}</li>
            <li>Square footage: {property.sqft}</li>
          </ul>
        </div>
      ))}
    </div>
  );
};

export default PropertyList;
