const axios = require("axios");

const options = {
  method: 'GET',
  url: 'https://the-cocktail-db.p.rapidapi.com/filter.php',
  params: {i: 'Gin'},
  headers: {
    'X-RapidAPI-Key': 'e8e6d1cb20msh3c038ddf785e626p1a61ccjsn3f1d9d72ccb6',
    'X-RapidAPI-Host': 'the-cocktail-db.p.rapidapi.com'
  }
};

const createCard = (cocktail) => {
  const card = document.createElement('div');
  card.className = 'card';

  const image = document.createElement('img');
  image.className = 'card-img-top';
  image.src = cocktail.strDrinkThumb;
  card.appendChild(image);

  const cardBody = document.createElement('div');
  cardBody.className = 'card-body';

  const title = document.createElement('h5');
  title.className = 'card-title';
  title.textContent = cocktail.strDrink;
  cardBody.appendChild(title);

  const description = document.createElement('p');
  description.className = 'card-text';
  description.textContent = cocktail.strInstructions;
  cardBody.appendChild(description);

  card.appendChild(cardBody);
  return card;
};

axios.request(options)
  .then(function (response) {
    const cocktails = response.data.drinks;
    const cardsContainer = document.getElementById('cards-container');
    cocktails.forEach(cocktail => {
      const card = createCard(cocktail);
      cardsContainer.appendChild(card);
    });
  })
  .catch(function (error) {
    console.error(error);
  });
